import React from "react";
import b from "./Banner.module.css";
import reactCSS from "reactcss";

const Banner = (props) => {
  const styles = reactCSS({
    default: {
      gradient: {
        width: "36px",
        height: "14px",
        borderRadius: "2px",
        background: `linear-gradient(-45deg, rgba(${props.col1.r},${props.col1.g},${props.col1.b},${props.col1.a}), rgba(${props.col2.r},${props.col2.g},${props.col2.b},${props.col2.a}))`,
      },
    },
  });

  return (
    <div className={b.banner}>
      <div className={b.background} style={styles.gradient}>
        <div className={b.link}>
          <a href={props.formData.url}></a>
        </div>
        <img src={props.formData.imgUrl} alt="" />
      </div>
    </div>
  );
};

export default Banner;
