import React from "react";
import b from "./Banner.module.css";
import reactCSS from "reactcss";

const Banner = (props) => {
  const Render = (inputData, col1, col2) => {
    const styles = reactCSS({
      default: {
        gradient: {
          width: "300px",
          height: "400px",
          borderRadius: "15px",
          background: `linear-gradient(-45deg, rgba(${col2.r},${col2.g},${col2.b},${col2.a}), rgba(${col1.r},${col1.g},${col1.b},${col1.a}))`,
        },
        link: {
          width: "300px" /* Ширина слоя */,
          height: "400px" /* Высота слоя */,
          position: "absolute",
          zIndex: "1",
        },
        a: {
          display: "block" /* Ссылка как блочный элемент */,
          textalign: "center" /* Выравнивание по центру */,
          height: "100%" /* Высота на весь слой */,
          color: "rgba(102, 102, 102, 0)" /* Цвет ссылки */,
          zIndex: "10000",
        },
        background: {
          width: "300px" /* Ширина слоя */,
          height: "400px" /* Высота слоя */,
          position: "relative",
          overflow: "hidden",
        },
        img: {
          position: "inherit",
          top: "20px",
          left: "50px",
          height: "55%",
          zIndex: "0",
        },
        text: {
          textalign: "left",
          top: "10px",
          left: "10px",
          height: "30%",
          fontsize: "22pt",
          color: "white",
          position: "relative",
        },
        textwrapper: {
          width: "90%",
          height: "40%",
          marginleft: "20px",
          position: "relative",
          overflow: "hidden",
        },
      },
    });
    return (
      <div className={b.banner}>
        <div className={b.background} style={styles.gradient}>
          <div className={b.link} style={styles.link}>
            <a href={inputData.url} style={styles.a}></a>
          </div>
          <img
            className={b.img}
            src={inputData.imgUrl}
            style={styles.img}
            alt=""
          />
          <div className={b.textwrapper} style={styles.textwrapper}>
            <h2 className={b.text} style={styles.text}>
              {inputData.text}
            </h2>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div>
      {Render(props.formData, props.col2, props.col1)}
      <div>
        {(e) => {
          props.func(Math.random());
        }}
      </div>
    </div>
  );
};

export default Banner;
