import React from "react";
import b from "./Banner.module.css";
import reactCSS from "reactcss";

const Banner = (props) => {
  const Render = (inputData, col1, col2) => {
    const styles = reactCSS({
      default: {
        gradient: {
          width: "300px",
          height: "400px",
          borderRadius: "15px",
          background: `linear-gradient(45deg, rgba(${col1.r},${col1.g},${col1.b},${col1.a}), rgba(${col2.r},${col2.g},${col2.b},${col2.a}))`,
        },
      },
    });
    return (
      <div className={b.banner}>
        <div className={b.background} style={styles.gradient}>
          <div className={b.link}>
            <a href={inputData.url}></a>
          </div>
          <img className={b.img} src={inputData.imgUrl} alt="" />
          <div className={b.textwrapper}>
            <h2 className={b.text}>{inputData.text}</h2>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div>
      {Render(props.formData, props.col1, props.col2)}
      <div>
        {(e) => {
          props.func(Math.random());
        }}
      </div>
    </div>
  );
};

export default Banner;
