import React from "react";
import i from "./InputData.module.css";

export function InputData(props) {
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const formData = new FormData((e.target = HTMLFormElement));
    // banner.create(
    //   formData.get("img"),
    //   formData.get("text"),
    //   formData.get("url")
    // );

    console.log(formData.get("img"), formData.get("text"), formData.get("url"));
  };
  return (
    <div className={i.input}>
      <form onSubmit={handleSubmit} className={i.form}>
        <p>
          <label htmlFor="img">URL картинки</label>
          <input type="text" id="img" name="img"></input>
        </p>
        <p>
          <label htmlFor="text">Текст баннера</label>
          <textarea id="text" name="text" cols="60" rows="6"></textarea>
        </p>
        <p>
          <label htmlFor="url">Ссылка баннера</label>
          <input id="url" name="url" type="text"></input>
        </p>
        <p>
          <b>Цвет фона</b>
          <br />
          <label className={i.switch}>
            <input type="checkbox" />
            <span className={`${i.slider} ${i.round}`}></span>
          </label>
          Градиент
        </p>
        <p>
          <input type="submit" value="Просмотр"></input>
        </p>
      </form>
    </div>
  );
}

export default InputData;
