import React from "react";
import i from "./InputData.module.css";

const InputData = (props) => {
  const [formData, updateFormData] = React.useState({
    imgUrl: "url",
    text: "text",
    url: "url",
    color: "red",
  });

  const handleChange = (e: React.FormEvent) => {
    updateFormData({
      // imgUrl: formData.get("imgUrl"),
      [e.target.imgUrl]: e.target.value,
    });
  };
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // const formData = new FormData((e.target = HTMLFormElement));
    // banner.create(
    //   formData.get("img"),
    //   formData.get("text"),
    //   formData.get("url")
    // );
    console.log("submittingChanges");
    console.log(formData);
  };
  return (
    <div className={i.input}>
      <form onSubmit={handleSubmit} className={i.form}>
        <p>
          <label htmlFor="imgUrl">URL картинки</label>
          <input
            type="text"
            id="imgUrl"
            name="imgUrl"
            onChange={handleChange}
          ></input>
        </p>
        <p>
          <label htmlFor="text">Текст баннера</label>
          <textarea
            id="text"
            name="text"
            cols="60"
            rows="6"
            onChange={handleChange}
          ></textarea>
        </p>
        <p>
          <label htmlFor="url">Ссылка баннера</label>
          <input
            id="url"
            name="url"
            type="text"
            onChange={handleChange}
          ></input>
        </p>
        <p>
          <b>Цвет фона</b>
          <br />
          <label className={i.switch}>
            <input type="checkbox" />
            <span className={`${i.slider} ${i.round}`}></span>
          </label>
          Градиент
        </p>
        <p>
          <input type="submit" value="Просмотр" onClick={handleSubmit}></input>
        </p>
      </form>
    </div>
  );
};

export default InputData;
