import React from "react";
import d from "./Export.module.css";
import html2canvas from "html2canvas";
import reactCSS from "reactcss";
import * as htmlToImage from "html-to-image";
import { toPng } from "html-to-image";
import ReactDOMServer from "react-dom/server";
import b from "./../Banner/Banner.module.css";
import * as fs from "file-saver";

const Export = (props) => {
  const example = () => {
    const styles = reactCSS({
      default: {
        gradient: {
          width: "300px",
          height: "400px",
          borderRadius: "15px",
          background: `linear-gradient(-45deg, rgba(241, 112, 19, 1), rgba(241,112,19,1))`,
        },
      },
    });
    return (
      <div className="Banner_background" style={styles.gradient}>
        <div className="Banner_link">
          <a href="http://mail.ru"></a>
        </div>
        <img className="Banner_img" src="/mustang.png" alt="" />
        <div className="Banner_textwrapper">
          <h2 className="Banner_text">text</h2>
        </div>
      </div>
    );
  };

  const handlePng = () => {
    const input = ReactDOMServer.renderToString(
      props.Render(props.formData, props.col2, props.col1)
    );
    const NewInput = document.getElementById("banner");

    htmlToImage.toPng(NewInput).then((canvas) => {
      window.saveAs(canvas, "banner.png");
    });
    return input;
  };

  const styles = reactCSS({
    default: {
      gradient: {
        width: "300px",
        height: "400px",
        borderRadius: "15px",
        background: `linear-gradient(-45deg, rgba(${props.col2.r},${props.col2.g},${props.col2.b},${props.col2.a}), rgba(${props.col1.r},${props.col1.g},${props.col1.b},${props.col1.a}))`,
      },
      link: {
        width: "300px" /* Ширина слоя */,
        height: "400px" /* Высота слоя */,
        position: "absolute",
        zIndex: "1",
      },
      a: {
        display: "block" /* Ссылка как блочный элемент */,
        textalign: "center" /* Выравнивание по центру */,
        height: "100%" /* Высота на весь слой */,
        color: "rgba(102, 102, 102, 0)" /* Цвет ссылки */,
        zIndex: "10000",
      },
      background: {
        width: "300px" /* Ширина слоя */,
        height: "400px" /* Высота слоя */,
        position: "relative",
        overflow: "hidden",
      },
      img: {
        position: "inherit",
        top: "20px",
        left: "50px",
        height: "55%",
        zIndex: "0",
      },
      text: {
        textalign: "left",
        top: "10px",
        left: "10px",
        height: "30%",
        fontsize: "22pt",
        color: "white",
        position: "relative",
      },
      textwrapper: {
        width: "90%",
        height: "40%",
        marginleft: "20px",
        position: "relative",
        overflow: "hidden",
      },
    },
  });

  return (
    <div className={d.exportwrapper}>
      <div className={b.banner}>
        <div className={b.background} style={styles.gradient} id="banner">
          <div className={b.link} style={styles.link}>
            <a href={props.formData.url} style={styles.a}></a>
          </div>
          <img
            className={b.img}
            src={props.formData.imgUrl}
            style={styles.img}
            alt=""
          />
          <div className={b.textwrapper} style={styles.textwrapper}>
            <h2 className={b.text} style={styles.text}>
              {props.formData.text}
            </h2>
          </div>
        </div>
        <div>
          <p>export png</p>
          <button
            onClick={handlePng}
            className="btn btn-primary btn-lg mx-auto"
          >
            Generate PNG
          </button>
          <p>{handlePng}</p>
        </div>
        <div>export json</div>
        <div>copy to buffer jsx </div>
      </div>
    </div>
  );
};

export default Export;
