import React from "react";
import { SketchPicker } from "react-color";
import i from "./InputData.module.css";
import reactCSS from "reactcss";

const InputData = (props) => {
  const initialFormData = Object.freeze({
    imgUrl: "url",
    text: "text",
    url: "url",
    color: "red",
  });

  const [formData, updateFormData] = React.useState(initialFormData);
  const [color1Info, updateColor1] = React.useState({
    displayColorPicker: false,
    color: {
      r: "241",
      g: "112",
      b: "19",
      a: "1",
    },
  });
  const [color2Info, updateColor2] = React.useState({
    displayColorPicker: false,
    color: {
      r: "241",
      g: "112",
      b: "19",
      a: "1",
    },
  });

  const handleChange = (e: React.FormEvent) => {
    updateFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const Color1Click = (e: React.FormEvent) => {
    updateColor1({
      ...color1Info,
      displayColorPicker: !color1Info.displayColorPicker,
    });
  };

  const Color2Click = (e: React.FormEvent) => {
    updateColor2({
      ...color2Info,
      displayColorPicker: !color2Info.displayColorPicker,
    });
  };

  const Color1Close = (e: React.FormEvent) => {
    updateColor1({
      ...color1Info,
      displayColorPicker: false,
    });
  };

  const Color2Close = (e: React.FormEvent) => {
    updateColor2({
      ...color2Info,
      displayColorPicker: false,
    });
  };

  const Color1Change = (color1) => {
    updateColor1({
      ...color1Info,
      color1: color1.rgb,
    });
  };
  const Color2Change = (color2) => {
    updateColor2({
      ...color2Info,
      color2: color2.rgb,
    });
  };

  const styles1 = reactCSS({
    default: {
      color: {
        width: "36px",
        height: "14px",
        borderRadius: "2px",
        background: `rgba(${color1Info.color.r}, ${color1Info.color.g}, ${color1Info.color.b}, ${color1Info.color.a})`,
      },
      swatch: {
        padding: "5px",
        background: "#fff",
        borderRadius: "1px",
        boxShadow: "0 0 0 1px rgba(0,0,0,.1)",
        display: "inline-block",
        cursor: "pointer",
      },
      popover: {
        position: "absolute",
        zIndex: "2",
      },
      cover: {
        position: "fixed",
        top: "0px",
        right: "0px",
        bottom: "0px",
        left: "0px",
      },
    },
  });

  const styles2 = reactCSS({
    default: {
      color: {
        width: "36px",
        height: "14px",
        borderRadius: "2px",
        background: `rgba(${color2Info.color.r}, ${color2Info.color.g}, ${color2Info.color.b}, ${color2Info.color.a})`,
      },
      swatch: {
        padding: "5px",
        background: "#fff",
        borderRadius: "1px",
        boxShadow: "0 0 0 1px rgba(0,0,0,.1)",
        display: "inline-block",
        cursor: "pointer",
      },
      popover: {
        position: "absolute",
        zIndex: "2",
      },
      cover: {
        position: "fixed",
        top: "0px",
        right: "0px",
        bottom: "0px",
        left: "0px",
      },
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log(formData);
    console.log(color1Info.color);
    console.log(color2Info.color);
  };
  return (
    <div className={i.input}>
      <form className={i.form}>
        <p>
          <label>URL картинки</label>
          <input type="text" name="imgUrl" onChange={handleChange}></input>
        </p>
        <p>
          <label>Текст баннера</label>
          <textarea
            name="text"
            cols="60"
            rows="6"
            onChange={handleChange}
          ></textarea>
        </p>
        <p>
          <label>Ссылка баннера</label>
          <input name="url" type="text" onChange={handleChange}></input>
        </p>
        <p>
          <div style={styles1.swatch} onClick={Color1Click}>
            <div style={styles1.color} />
          </div>
          {color1Info.displayColorPicker ? (
            <div style={styles1.popover}>
              <div style={styles1.cover} onClick={Color1Close} />
              <SketchPicker color={color1Info.color} onChange={Color1Change} />
            </div>
          ) : null}

          <div style={styles2.swatch} onClick={Color2Click}>
            <div style={styles2.color} />
          </div>
          {color2Info.displayColorPicker ? (
            <div style={styles2.popover}>
              <div style={styles2.cover} onClick={Color2Close} />
              <SketchPicker color={color2Info.color} onChange={Color2Change} />
            </div>
          ) : null}
        </p>
        <p>
          <button value="Просмотр" onClick={handleSubmit}>
            Просмотр
          </button>
        </p>
      </form>
    </div>
  );
};

export default InputData;
