import React from "react";
import d from "./Export.module.css";
import html2canvas from "html2canvas";
import reactCSS from "reactcss";
import * as htmlToImage from "html-to-image";
import { toPng } from "html-to-image";
import ReactDOMServer from "react-dom/server";

const Export = (props) => {
  const example = () => {
    const styles = reactCSS({
      default: {
        gradient: {
          width: "300px",
          height: "400px",
          borderRadius: "15px",
          background: `linear-gradient(-45deg, rgba(241, 112, 19, 1), rgba(241,112,19,1))`,
        },
      },
    });
    return (
      <div className="Banner_background" style={styles.gradient}>
        <div className="Banner_link">
          <a href="http://mail.ru"></a>
        </div>
        <img className="Banner_img" src="/mustang.png" alt="" />
        <div className="Banner_textwrapper">
          <h2 className="Banner_text">text</h2>
        </div>
      </div>
    );
  };

  const handlePng = () => {
    const input = ReactDOMServer.renderToString(
      props.Render(props.formData, props.col2, props.col1)
    );
    // htmlToImage.toPng(input).then((blob) => {
    //   const img = new Image();
    //   img.src = blob;
    //   document.body.appendChild(img);
    // });
    const img = input.toDataURL("image/png");
    // .catch(function (error) {
    //   console.error('azaza error', error);
    // })
    return input;
  };

  return (
    <div className={d.exportwrapper}>
      <div>
        <p>export png</p>
        <button onClick={handlePng} className="btn btn-primary btn-lg mx-auto">
          Generate PNG
        </button>
        <p>{handlePng}</p>
      </div>
      <div>export json</div>
      <div>copy to buffer jsx </div>
    </div>
  );
};

export default Export;
